import React, {useState, useEffect} from 'react';
import './Artists.css';
import Artisttem from './ArtistItem';
import axios from 'axios'

function Recents() {
  const [recents, setRecents] = useState([])
  let count = 1;
  useEffect(() => {
    axios.get('http://localhost:5000/recents').then(response => {
      setRecents(response.data.items);
      console.log(response.data.items);
    });
  }, [])

  return (
    <div className='cards'>
      <h1>Top Artists</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {
              recents === undefined ? (
                <p>Fetching your recent tracks!</p>
              ) : (
              recents.map(recents => (
              <Artisttem
                src={recents.recents.images[0].url}
                text={recents.recents.name}
                label={count++}
                path={recents.recents.external_urls.spotify}
                />
              )))
            }
          </ul>
        </div>
      </div>
    </div>
  );

}

export default Recents;
